import "core-js/modules/es.array.push.js";
export default {
  name: 'TurismoView',
  data() {
    return {
      turismo: [],
      selectedType: ""
    };
  },
  created() {
    axios.get('/json/turismo.json').then(response => {
      this.turismo = response.data;
    }).catch(error => {
      console.error(error);
    });
  },
  computed: {
    uniqueTypes() {
      return [...new Set(this.turismo.map(turismo => turismo.type))];
    },
    filteredTurismo() {
      if (this.selectedType === "") {
        return this.turismo;
      } else {
        return this.turismo.filter(turismo => turismo.type === this.selectedType);
      }
    }
  },
  methods: {
    deleteTurismo(index) {
      this.turismo.splice(index, 1);
    },
    addTurismo() {
      this.turismo.push(this.newTurismo);
      this.newTurismo = {
        name: "",
        type: "",
        ability: ""
      };
    }
  }
};
